INSERT INTO `wp_posts` (`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES (331,9,'2013-05-15 18:40:29','2013-05-15 18:40:29','Mid-Month Issuer Review of the S&amp;P U.S. Issued High Yield Corporate Bond Index  ','','inherit','open','open','','321-revision-2','','','2013-05-15 18:40:29','2013-05-15 18:40:29','The index is <b>up 0.42%</b> month-to-date.  Here is a performance review of the top &amp; bottom 10 issues within the index.\n\n<b><span style=\"text-decoration: underline\">Top 10 Issues</span></b>\n<ul>\n	<li>May has been a good month for <b>MBIA</b> so far.  The insurer settled a 2008 legal dispute with Bank of America concerning bad mortgage debt.  The $1.7 billion settlement later led to a Standard &amp; Poor’s rating upgrade, which moved the issuer’s senior unsecured debt from B- to BBB.</li>\n	<li><b>Momentive Performance’s</b> first quarter results of slightly lower sales but improving operating incomes, coupled with refinancing activities for a $270 million asset-based revolving loan and a $75 million revolving credit facility, moved this issuer’s bonds up.</li>\n	<li><b>TXU</b> bonds improved as Energy Futures Holdings Corp. looks to win support for a bankruptcy restructuring deal.</li>\n</ul>\n<img class=\"alignleft size-medium wp-image-323\" alt=\"Top 10 Issues\" src=\"https://dev.indexologyblog.com/wp-content/uploads/2013/05/10-UP-600x225.png\" width=\"600\" height=\"225\" />\n\n&nbsp;\n\n&nbsp;\n\n&nbsp;\n\n&nbsp;\n\n&nbsp;\n\n&nbsp;\n\n<b><span style=\"text-decoration: underline\">Bottom 10 Issues</span></b>\n<ul>\n	<li><b>Cengage’s</b> CEO has publically announced that the company may need to file for Chapter 11 and that no decision on a restructuring has been made.</li>\n	<li><b>Physiotherapy Associates</b> is in monetary default as a coupon payment has been missed.</li>\n	<li><b>Harrah’s Operating Company</b> bonds dropped as Ceasars Entertainment missed estimates due to revenue declines in their main markets.</li>\n</ul>\n<img class=\"alignleft size-medium wp-image-324\" alt=\"Bottom 10 Issues\" src=\"https://dev.indexologyblog.com/wp-content/uploads/2013/05/10-Down-600x225.png\" width=\"600\" height=\"225\" />\n\n&nbsp;','',321,'https://dev.indexologyblog.com/2013/05/15/321-revision-2/',0,'revision','',0);
