INSERT INTO `wp_posts` (`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES (605,5,'2013-06-20 13:17:44','2013-06-20 13:17:44','Who\'s complaining, who\'s speculating, who\'s hedging and who\'s to blame?','','inherit','open','open','','597-revision-5','','','2013-06-20 13:17:44','2013-06-20 13:17:44','JOIN US FOR OUR 7TH ANNUAL COMMODITIES SEMINAR!\r\n\r\n<strong><strong>Managing Commodities in Modern Times:</strong><b>\r\n<strong>Hard-Won Lessons After 160 Years of Trial &amp; Error</strong></b></strong>\r\n\r\nRegister here: <a style=\"line-height: 1.714285714;font-size: 1rem\" title=\"S&amp;P Dow Jones Indices Commodity Seminar\" href=\"http://now.eloqua.com/es.asp?s=795&amp;e=734158&amp;elq=3ee4e8ff7af64f7f967d02f6578101cf&amp;goback=%2Enpv_43648778_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1%2Egmp_2426754%2Egde_2426754_member_251560389\" target=\"_blank\">http://now.eloqua.com/es.asp?s=795&amp;e=734158&amp;elq=3ee4e8ff7af64f7f967d02f6578101cf&amp;goback=%2Enpv_43648778_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1_*1%2Egmp_2426754%2Egde_2426754_member_251560389</a>\r\n\r\nS&amp;P Dow Jones Indices invites you to this half-day complimentary seminar which has become Europe\'s annual meeting point for commodity aficionados for close to a decade.\r\n\r\nJoin us and other leading industry professionals for an afternoon of education and networking opportunities. Take a front row seat to walk away with valuable insights into current trends and issues under the umbrella of who\'s complaining, who\'s hedging, who\'s speculating, who\'s to blame and finally, where do I go?\r\n\r\n<strong st