INSERT INTO `wp_posts` (`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES (966,4,'2013-07-26 14:47:50','2013-07-26 14:47:50','What Municipalities are Higher Risk?','','publish','open','open','','what-municipalities-are-higher-risk','','','2013-08-02 14:09:35','2013-08-02 14:09:35','<i>Data as of July 25, 2013</i>\r\n\r\n<b>What Municipalities are Higher Risk?</b>\r\n\r\nThe bankruptcy of the <b>City of Detroit</b> has raised questions about what other municipalities might be the next to focus on.  In general, bonds with higher yields are riskier than bonds with lower yields.  Using bond yields as the sole variable, the following general obligation municipal bond issuers have the highest yielding bond issues<b>*</b> in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-municipal-bond-index\"><b>S&amp;P Municipal Bond Index</b></a>:\r\n\r\nMaverick County, TX\r\n\r\nScranton, PA\r\n\r\nCentral Falls, RI\r\n\r\nNorthern Mariana Islands\r\n\r\nWoonsocket, RI\r\n\r\nBellwood, IL\r\n\r\nRiverdale, IL\r\n\r\nHarvey, IL\r\n<ul>\r\n	<li>The average yield to worst for investment grade municipal bonds is 3.12% (<a href=\"http://us.spindices.com/indices/fixed-income/sp-national-amt-free-municipal-bond-index\"><b>S&amp;P National AMT-Free Municipal Bond Index</b></a>).  The yields of the bonds from these issuers range from 7% to over 9.25%.</li>\r\n	<li>The <a href=\"http://us.spindices.com/indices/fixed-income/sp-municipal-bond-index\"><b>S&amp;P Municipal Bond Index</b></a> is a broad benchmark index that tracks over 70,000 bond issues from over 20,000 municipal bond offerings representing over $1.4 trillion in par value.</li>\r\n	<li>None of these bond issues are included in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-national-amt-free-municipal-bond-index\"><b>S&amp;P National AMT-Free Municipal Bond Index</b></a> which tracks only investment grade bonds.</li>\r\n</ul>\r\n<b><i>*</i></b><i>Review focused on bonds issued by and are the general obligation of counties and cities. No district, special tax or land backed bonds were reviewed.  Only bonds that have not previously defaulted on principal and or interest are listed. </i>\r\n\r\n<b>Market Performance:</b><b>  </b>\r\n<ul>\r\n	<li>The high profile default of the <b>City of Detroit</b> along with heavy fund outflows have made the summer of 2013 a hard one for the municipal bond market.</li>\r\n	<li>Investment grade municipal bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-national-amt-free-municipal-bond-index\"><b>S&amp;P National AMT-Free Municipal Bond Index</b></a> saw yields rise by 38 bps this month driving bond prices down and recording a negative monthly return of 1.54%. By comparison, bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-us-issued-investment-grade-corporate-bond-index\"><b>S&amp;P U.S. Issued Investment Grade Corporate bond Index</b></a> have seen yields come down by 6 bps holding returns in positive territory.</li>\r\n	<li>High yield municipal bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-municipal-bond-high-yield-index\"><b>S&amp;P Municipal Bond High Yield Index</b></a> recorded yields rising by 36 bps this month driving bond prices down and recording a negative monthly return of 2.65%. High yield corporate bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-us-issued-high-yield-corporate-bond-index\"><b>S&amp;P U.S. Issued High Yield Corporate Bond Index</b></a> have seen yields come down and prices rise driving a positive 1.83% return.</li>\r\n</ul>\r\n&nbsp;','',0,'https://dev.indexologyblog.com/?p=966',0,'post','',0);
INSERT INTO `wp_posts` (`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) VALUES (967,4,'2013-07-26 14:39:50','2013-07-26 14:39:50','What Municipalities are Higher Risk?','','inherit','open','open','','966-revision','','','2013-07-26 14:39:50','2013-07-26 14:39:50','<i>Data as of July 25, 2013</i>\n\n<b>What Municipalities are Higher Risk?</b>\n\nThe bankruptcy of the <b>City of Detroit</b> has raised questions about what other municipalities might be the next to focus on.  In general, bonds with higher yields are riskier than bonds with lower yields.  Using bond yields as the sole variable, the following general obligation municipal bond issuers have the highest yielding bond issues<b>*</b> in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-municipal-bond-index\"><b>S&amp;P Municipal Bond Index</b></a>:\n\nMaverick County, TX\n\nScranton, PA\n\nCentral Falls, RI\n\nNorthern Mariana Islands\n\nWoonsocket, RI\n\nBellwood, IL\n\nRiverdale, IL\n\nHarvey, IL\n<ul>\n	<li>The average yield to worst for investment grade municipal bonds is 3.12% (<a href=\"http://us.spindices.com/indices/fixed-income/sp-national-amt-free-municipal-bond-index\"><b>S&amp;P National AMT-Free Municipal Bond Index</b></a>).  The yields of the bonds from these issuers range from 7% to over 9.25%.</li>\n	<li>The <a href=\"http://us.spindices.com/indices/fixed-income/sp-municipal-bond-index\"><b>S&amp;P Municipal Bond Index</b></a> is a broad benchmark index that tracks over 70,000 bond issues from over 20,000 municipal bond offerings representing over $1.4trillion in par value.</li>\n	<li>None of these bond issues are included in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-national-amt-free-municipal-bond-index\"><b>S&amp;P National AMT-Free Municipal Bond Index</b></a> which tracks only investment grade bonds.</li>\n</ul>\n<b><i>*</i></b><i>Review focused on bonds issued by and are the general obligation of counties and cities. No district, special tax or land backed bonds were reviewed.  Only bonds that have not previously defaulted on principal and or interest are listed. </i>\n\n<b>Market Performance:</b><b>  </b>\n<ul>\n	<li>The high profile default of the <b>City of Detroit</b> along with heavy fund outflows have made the summer of 2013 a hard one for the municipal bond market.</li>\n	<li>Investment grade municipal bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-national-amt-free-municipal-bond-index\"><b>S&amp;P National AMT-Free Municipal Bond Index</b></a> saw yields rise by 38 bps this month driving bond prices down and recording a negative monthly return of 1.54%. By comparison, bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-us-issued-investment-grade-corporate-bond-index\"><b>S&amp;P U.S. Issued Investment Grade Corporate bond Index</b></a> have seen yields come down by 6 bps holding returns in positive territory.</li>\n	<li>High yield municipal bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-municipal-bond-high-yield-index\"><b>S&amp;P Municipal Bond High Yield Index</b></a> recorded yields rising by 36 bps this month driving bond prices down and recording a negative monthly return of 2.65%. High yield corporate bonds tracked in the <a href=\"http://us.spindices.com/indices/fixed-income/sp-us-issued-high-yield-corporate-bond-index\"><b>S&amp;P U.S. Issued High Yield Corporate Bond Index</b></a> have seen yields come down and prices rise driving a positive 1.83% return.</li>\n</ul>\n&nbsp;','',966,'https://dev.indexologyblog.com/2013/07/26/966-revision/',0,'revision','',0);
